import { useState } from "react"
import SelectButton from "../SelectButton";
import { FormWrapper } from "../FormWrapper";

type Props = {
    gender: string
}

export function StepOne({ gender }: Props) {
    const [selectedValue, setSelectedValue] = useState("");

    return (
        <FormWrapper title="Gender">
            <p className="desc">
                Tell us your gender to better personalize your workout
            </p>
            <SelectButton info="Female" selected={selectedValue === "Female"} onSelect={() => setSelectedValue("Female")} />
            <SelectButton info="Male" selected={selectedValue === "Male"} onSelect={() => setSelectedValue("Male")} />
            <SelectButton info="Neither" selected={selectedValue === "Neither"} onSelect={() => setSelectedValue("Neither")} />
            <SelectButton info="Prefer not to say" selected={selectedValue === "Prefer not to say"} onSelect={() => setSelectedValue("Prefer not to say")} />
        </FormWrapper>
    )
}
