import { useState } from "react"
import SelectButton from "../SelectButton";
import { FormWrapper } from "../FormWrapper";

type UserData = {
    targetArea: string
}

type Props = UserData & {
    updateFields: (fields: Partial<UserData>) => void
}

export function StepTwo({ targetArea, updateFields }: Props) {
    const [selectedValue, setSelectedValue] = useState("");
    setSelectedValue(targetArea)

    return (
        <FormWrapper title="Muscle">
            <p className="desc">
                What areas of your body do you want to target for this workout?
            </p>
            <SelectButton info="Shoulders" selected={selectedValue === "Shoulders"} onSelect={() => setSelectedValue("Shoulders")} />
            <SelectButton info="Chest" selected={selectedValue === "Chest"} onSelect={() => setSelectedValue("Chest")} />
            <SelectButton info="Triceps" selected={selectedValue === "Triceps"} onSelect={() => setSelectedValue("Triceps")} />
            <SelectButton info="Biceps" selected={selectedValue === "Biceps"} onSelect={() => setSelectedValue("Biceps")} />
            <SelectButton info="Back" selected={selectedValue === "Back"} onSelect={() => setSelectedValue("Back")} />
            <SelectButton info="Legs" selected={selectedValue === "Legs"} onSelect={() => setSelectedValue("Legs")} />
        </FormWrapper>
    )
}
