import React from "react";
import "./Hours.css";

interface HoursProps {
  hoursData: { day: string; hours: number }[];
  totalHours: number;
}

function Hours({ hoursData, totalHours }: HoursProps) {
  const maxHours = Math.max(...hoursData.map((day) => day.hours)) || 1;

  return (
    <div className="hours-chart">
      <h2 className="section-title">Hours</h2>
      <p className="section-subtitle">{totalHours} hours</p>
      <div className="bars-container">
        {hoursData.map((data) => (
          <div key={data.day} className="bar-container">
            <div
              className="bar"
              style={{
                height: `${(data.hours / maxHours) * 100}%`,
                backgroundColor: data.hours > 0 ? "#1d2a42" : "#f0f0f5",
                color: data.hours > 0 ? "#ffd700" : "#8b8b8b",
              }}
            >
              {data.hours > 0 && (
                <span className="hours-label">{data.hours}hr</span>
              )}
            </div>
            <span className="day-label">{data.day}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Hours;
