import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate, useLocation } from 'react-router-dom';
import { CreateWorkout } from './views/CreateWorkout'
import "./App.css";
import Navbar from "./Navbar/Navbar";
import Home from "./views/Home";
import { FindAWorkout } from "./views/FindAWorkout";
import MeetOthers from "./components/dummy-pages/MeetOthers";
import { ProfileTab } from "./views/ProfileTab";
import { ProfilePage } from "./views/ProfilePage";
import { RecommendedWorkouts } from "./views/RecommendedWorkouts";
import { ExercisesPage } from "./components/ExercisesPage";
import { CreateAccount } from "./views/createAccount";
import { SignIn } from "./views/signIn";

const AppContent = () => {
    const location = useLocation();
    const [activeView, setActiveView] = useState('/');
  
    useEffect(() => {
      setActiveView(location.pathname);
    }, [location]);
  
    return (
      <div className="app-container">
        {/* Navbar included for these routes */}
        <Routes>
          <Route path="/" 
              element={
                <>
                  <Home />
                  <Navbar />
                </>
              }
              />
              <Route path="/findworkout"
              element={
                <>
                  <FindAWorkout />
                  <Navbar />
                </>
              }
              />
              <Route path="/meetothers"
              element={
                <>
                  <MeetOthers />
                  <Navbar />
                </>
              }
              />
              <Route path="/profile"
              element={
                <div className={`profileView ${activeView === "/profile" ? "active" : "inactive"}`}>
                  <ProfileTab />
                  <Navbar />
                </div>
              }
              />
              <Route path="/edit-profile"
              element={
                <div className={`profileView ${activeView === "/edit-profile" ? "active" : "inactive"}`}>
                  <ProfilePage />
                </div>
              }
              />
          </Routes>
  
          {/* Routes that don't include the Navbar */}
          <Routes>
              <Route path="/recommendedWorkouts" element={<RecommendedWorkouts />} />
              <Route path="/exercises" element={<ExercisesPage />} />
              <Route path="/createaccount" element={<CreateAccount />} />
              <Route path="/signin" element={<SignIn />} />
              <Route path="/createWorkout" element={<CreateWorkout />} />
          </Routes>
      </div>
    );
  };

const App: React.FC = () => (
  <Router>
    <AppContent />
  </Router>
);

export default App;
